import {createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import axios from "axios";

export const fetchProducts = createAsyncThunk('products/fetchProducts', async (params) => {
  const {shopBy, searchBy, sortBy, currentPage} = params
  const {data} = await axios.get(`https://62f79a7bab9f1f8e890019ee.mockapi.io/products?${shopBy}&page=${currentPage}&${sortBy}&limit=8&${searchBy}&order=asc`
  )
  return data
})

const initialState = {
  items: [],
  // status:
}

const productsSlice = createSlice({
  name: 'products',
  initialState,

  reducers: {
    setProducts(state, action) {
      state.items = action.payload
    }
  },
  extraReducers: {
    [fetchProducts.pending]: () => {
      console.log('loading')
    },
    [fetchProducts.fulfilled]: (state, action) => {
      state.items = action.payload
    },
    [fetchProducts.rejected]: () => {
      console.log('404')
    }
  }
})

export const {setProducts} = productsSlice.actions
export default productsSlice.reducer
