import React from 'react';

const Shops = () => {
  const shops = ['All', 'Carrefour', 'Masterino', 'Dolfin']

  const [activeShop, setActiveShop] = React.useState('All')

  const clickActiveShop = (title) => {
    setActiveShop(title)
    console.log(title)
  }
  return (
    <div className="categories">
      <ul>
        {
          shops.map((shopTitle) => <li
              className={activeShop === shopTitle ? 'active' : ''}
              onClick={(title) => clickActiveShop(title)}
            >
              {shopTitle}
            </li>
          )
        }
        {/*<li className="active">All</li>*/}
        {/*<li>Carrefour</li>*/}
        {/*<li>Masterino</li>*/}
        {/*<li>Dolfin</li>*/}
      </ul>
    </div>
  );
};

export default Shops;
