import React from 'react';

const Shops = () => {
  const shops = ['All', 'Carrefour', 'Masterino', 'Dolfin']

  const [activeShop, setActiveShop] = React.useState(0)

  const onClickShop = (idx) => {
    setActiveShop(idx)
    console.log(idx)
  }
  return (
    <div className="categories">
      <ul>
        {
          shops.map((shopTitle, index) => <li
              className={activeShop === index ? 'active' : ''}
              key={shopTitle}
            >
              {shopTitle} 
            </li>
          )
        }
        {/*<li className="active">All</li>*/}
        {/*<li>Carrefour</li>*/}
        {/*<li>Masterino</li>*/}
        {/*<li>Dolfin</li>*/}
      </ul>
    </div>
  );
};

export default Shops;
