import {createSlice} from "@reduxjs/toolkit";

const initialState = {
  shop: '',
  sort: {
    name: 'price',
    sortProperty: 'price'
  }
}

const filterSlice = createSlice({
  name: 'filter',
  initialState,

  reducers: {
    setShopName(state, action) {
      state.shop = action.payload
    }
  }
})

export const {setShopName} = filterSlice.actions

export default filterSlice.reducer
