import React from 'react';
import Shops from "../components/Shops";
import Sort from "../components/Sort";
import ProductBlockSkeleton from "../components/productBlock/ProductBlockSkeleton";
import ProductBlock from "../components/productBlock/ProductBlock";

const Home = ({searchValue}) => {

  const [activeShop, setActiveShop] = React.useState(0)

  const [items, setItems] = React.useState([])
  const [isLoading, setIsLoading] = React.useState(true)

  const search = searchValue ? `search=${searchValue}` : ''

  React.useEffect(() => {
    setIsLoading(true)
    fetch(`https://62f79a7bab9f1f8e890019ee.mockapi.io/products?search=${search}`)
      .then(data => data.json())
      .then(data => {
        setItems(data)
        setIsLoading(false)
      })
      .catch(error => console.log(error.message()))
    window.scrollTo(0, 0)
  }, [searchValue])

  const getShopProducts = (title) => {
    setIsLoading(true)
    if (title === 'All') {
      fetch(allProducts)
        .then(data => data.json())
        .then(data => {
          setItems(data)
          setIsLoading(false)
        })
        .catch(error => console.log(error.message()))
      window.scrollTo(0, 0)
    } else {
      fetch(`https://62f79a7bab9f1f8e890019ee.mockapi.io/products?shop=${title}`)
        .then(data => data.json())
        .then(data => {
          setItems(data)
          setIsLoading(false)
        })
        .catch(error => console.log(error.message()))
      window.scrollTo(0, 0)
    }
  }


  const choseShop = (i, title) => {
    getShopProducts(title)
    setActiveShop(i)
  }


  const skeletons = [...new Array(9)].map((_, idx) => <ProductBlockSkeleton key={idx}/>)

  // const products = items.map(product => <ProductBlock title={product.title} imageUrl={product.imageUrl}
  //                                                     price={product.price} measure={product.measure}
  //                                                     key={product.title}/>)

  const filteredProducts = items
    .filter(product => {
      if (product.title.toLowerCase().includes(searchValue.toLowerCase())) {
        return true
      }
      return false
    })
    .map(product => <ProductBlock
      title={product.title} imageUrl={product.imageUrl}
      price={product.price} measure={product.measure}
      key={product.title}/>)


  return (
    <>
      <div className="content__top">
        <Shops activeShop={activeShop} onClickShop={(i, title) => choseShop(i, title)}/>
        <Sort/>
      </div>
      <h2 className="content__title">All goods</h2>
      <div className="content__items">

        {
          isLoading
            ? skeletons
            : filteredProducts
        }
      </div>
    </>
  );
};

export default Home;
