import React from 'react';

const Shops = ({activeShop, onClickShop}) => {
  console.log(activeShop)
  // const carrefourProducts = 'https://62f79a7bab9f1f8e890019ee.mockapi.io/products?shop=Carrefour'
  // const masterProducts = 'https://62f79a7bab9f1f8e890019ee.mockapi.io/products?shop=Masterino'
  // const dolphinProducts = 'https://62f79a7bab9f1f8e890019ee.mockapi.io/products?shop=Dolphin'

  const shops = ['All', 'Carrefour', 'Masterino', 'Delfino']

  //const [activeShop, setActiveShop] = React.useState(0)

  // React.useEffect(()=>{
  //   fetch(str)
  //     .then(data=> data.json())
  //     .then(products => set)
  // })

  // const onClickShop = (i) => {
  //   setActiveShop(i)
  // }
  return (
    <div className="shops">
      <ul>
        {
          shops.map((shopTitle, index) => <li
              className={activeShop === index ? 'active' : ''}
              key={shopTitle}
              onClick={() => onClickShop(index)}
            >
              {shopTitle}
            </li>
          )
        }
      </ul>
    </div>
  );
};

export default Shops;
