import React from 'react';

const Shops = () => {
  const shops = ['All', 'Carrefour', 'Masterino', 'Dolfin']

  const [activeShop, setActiveShop] = React.useState('Shop')

  const clickActiveShop = (i) => setActiveShop(i)
  return (
    <div className="categories">
      <ul>
        {
          shops.map((shopTitle, idx) => {
            return `<li className={${activeShop} === ${shopTitle} ? 'active' : ''} >${shopTitle}</li>`
          })
        }
        {/*<li className="active">All</li>*/}
        {/*<li>Carrefour</li>*/}
        {/*<li>Masterino</li>*/}
        {/*<li>Dolfin</li>*/}
      </ul>
    </div>
  );
};

export default Shops;
