import React from 'react';
import debounce from 'lodash.debounce'

import styles from './search.module.scss'
import {SearchContext} from "../../App";

const Search = () => {

  const {searchValue, setSearchValue} = React.useContext(SearchContext)

  const testDebounce = debounce(() => {
    console.log('Hello')
  }, 1000)

  const inputRef = React.useRef()

  const onClear = () => {
    setSearchValue('')
    inputRef.current.focus()
  }

  const onChangeSearch = debounce((e) => {
    setSearchValue(e.target.value)
  }, 1000)

  return (
    <div className={styles.main}>
      <svg className={styles.iconSearch} xmlns="http://www.w3.org/2000/svg"
           enableBackground="new 0 0 32 32" id="Glyph" version="1.1" viewBox="0 0 32 32"
      >
        <path d="M27.414,24.586l-5.077-5.077C23.386,17.928,24,16.035,24,14c0-5.514-4.486-10-10-10S4,8.486,4,14
           s4.486,10,10,10c2.035,0,3.928-0.614,5.509-1.663l5.077,5.077c0.78,0.781,2.048,0.781,2.828,0
           C28.195,26.633,28.195,25.367,27.414,24.586z M7,14c0-3.86,3.14-7,7-7s7,3.14,7,7s-3.14,7-7,7S7,17.86,7,14z"
              id="XMLID_223_"/>
      </svg>
      <input
        ref={inputRef}
        className={styles.input}
        type="text"
        placeholder="Search product ..."
        value={searchValue}
        //onChange={e => setSearchValue(e.target.value)}
        onChange={() => onChangeSearch()}
      />
      {searchValue && (<svg
        className={styles.iconClose}
        onClick={() => onClear()}
        xmlns="http://www.w3.org/2000/svg" height="512px" id="Layer_1"
        version="1.1" viewBox="0 0 512 512" width="512px">
        <path d="M437.5,386.6L306.9,256l130.6-130.6c14.1-14.1,14.1-36.8,0-50.9c-14.1-14.1-36.8-14.1-50.9,0L256,205.1L125.4,74.5
        c-14.1-14.1-36.8-14.1-50.9,0c-14.1,14.1-14.1,36.8,0,50.9L205.1,256L74.5,386.6c-14.1,14.1-14.1,36.8,0,50.9
        c14.1,14.1,36.8,14.1,50.9,0L256,306.9l130.6,130.6c14.1,14.1,36.8,14.1,50.9,0C451.5,423.4,451.5,400.6,437.5,386.6z"/>
      </svg>)
      }

    </div>
  );
};

export default Search;
