import React from 'react';
import styles from './remove.module.scss'

const RemoveCartProduct = () => {
  const [showRemove, setShowRemove] = React.useState(false)

  const hidePopup = () => {
    setShowRemove(false)
  }

  return (
    <div className={showRemove ? styles.layout : styles.layoutHide}>
      <div className={styles.popup}>
        <p className={styles.title}>Do you really want to remove TOMATO ?</p>
        <div className={styles.buttons}>
          <button className="button">Yes, I'm sure</button>
          <button
            className="button button--tomato"
            onClick={hidePopup}
          >
            No
          </button>
        </div>
      </div>
    </div>
  );
};

export default RemoveCartProduct;
