import {createSlice} from "@reduxjs/toolkit";

const initialState = {
  cartProducts: [],
  totalPrice: 0,
  countPs: 0,
  countKg: 0,
  totalCount: 0,
}

const cartSlice = createSlice({
  name: 'cart',
  initialState,

  reducers: {

    addCartItem(state, action) {
      const findItem = state.cartProducts.find(item => item.title === action.payload.title)

      if (findItem) {
        findItem.count++
      } else {
        state.cartProducts.push({
          ...action.payload,
          count: 1
        })
      }
      state.totalPrice = state.cartProducts.reduce((sum, product) => sum + (product.price * product.count), 0)
      state.totalCount = state.cartProducts.reduce((sum, product) => sum + product.count, 0)
    },

    removeCartItem(state, action) {
      state.cartProducts = state.cartProducts.filter(product => product.name !== action.payload)
    },

    countSumKg(state, action) {
      state.countKg = state.countKg + action.payload
    },

    countSumPs(state, action) {
      state.countPs = state.countPs + action.payload
    },

    setTotalCount(state) {
      state.totalCount = state.countPs + state.countKg
    },

    clearItems(state) {
      state.cartProducts = []
    },
  }
})

export const {addCartItem, removeCartItem,} = cartSlice.actions
export default cartSlice.reducer
