import {createSlice} from "@reduxjs/toolkit";

const initialState = {
  cartProducts: [],
  totalPrice: 0,
  totalCountPs: 0,
  totalCountKg: 0
}

const cartSlice = createSlice({
  name: 'cart',
  initialState,

  reducers: {
    addCartItem(state, action) {
      state.cartProducts.push(action.payload)
    },

    removeCartItem(state, action) {
      state.cartProducts = state.cartProducts.filter(product => product.name !== action.payload)
    },

    countSumKg(state, action) {
      state.totalCountKg = state.totalCountKg + action.payload
    },

    countSumPs(state, action) {
      state.totalCountPs = state.totalCountPs + action.payload
    },

    clearItems(state) {
      state.cartProducts = []
    }
  }
})

export const {addCartItem, removeCartItem} = cartSlice.actions
export default cartSlice.reducer
