import {createSlice} from "@reduxjs/toolkit";

const initialState = {
  cartProducts: [],
  totalPrice: 0,
  countPs: 0,
  countKg: 0,
  totalCount: 0,
}

const cartSlice = createSlice({
  name: 'cart',
  initialState,

  reducers: {

    updateCountValues(state) {

      const itemsKg = state.cartProducts.filter(item => item.measure === 'kilograms')
      const itemsPs = state.cartProducts.filter(item => item.measure === 'peaces')

      state.totalPrice = state.cartProducts.reduce((sum, product) => sum + (product.price * product.count), 0)
      state.totalCount = state.cartProducts.reduce((sum, product) => sum + product.count, 0)

      state.countKg = itemsKg.reduce((sum, product) => sum + product.count, 0)
      state.countPs = itemsPs.reduce((sum, product) => sum + product.count, 0)
    },

    addCartItem(state, action) {
      const findItem = state.cartProducts.find(item => item.title === action.payload.title)

      if (findItem) {
        findItem.count++
      } else {
        state.cartProducts.push({
          ...action.payload,
          count: 1
        })
      }

      // this.updateCountValues(state)
      // const itemsKg = state.cartProducts.filter(item => item.measure === 'kilograms')
      // const itemsPs = state.cartProducts.filter(item => item.measure === 'peaces')
      //
      // state.totalPrice = state.cartProducts.reduce((sum, product) => sum + (product.price * product.count), 0)
      // state.totalCount = state.cartProducts.reduce((sum, product) => sum + product.count, 0)
      //
      // state.countKg = itemsKg.reduce((sum, product) => sum + product.count, 0)
      // state.countPs = itemsPs.reduce((sum, product) => sum + product.count, 0)
    },

    minusCartItem(state, action) {

      const findItem = state.cartProducts.find(item => item.title === action.payload)
      if (findItem) {
        findItem.count--
      }

      // this.updateCountValues()
      // const itemsKg = state.cartProducts.filter(item => item.measure === 'kilograms')
      // const itemsPs = state.cartProducts.filter(item => item.measure === 'peaces')
      //
      // state.totalPrice = Math.floor(state.cartProducts.reduce((sum, product) => sum + (product.price * product.count), 0))
      // state.totalCount = state.cartProducts.reduce((sum, product) => sum + product.count, 0)
      //
      // state.countKg = itemsKg.reduce((sum, product) => sum + product.count, 0)
      // state.countPs = itemsPs.reduce((sum, product) => sum + product.count, 0)
    },

    removeCartItem(state, action) {

      // this.updateCountValues(state)
      // const itemsKg = state.cartProducts.filter(item => item.measure === 'kilograms')
      // const itemsPs = state.cartProducts.filter(item => item.measure === 'peaces')
      //
      // state.totalPrice = state.cartProducts.reduce((sum, product) => sum + (product.price * product.count), 0)
      // state.totalCount = state.cartProducts.reduce((sum, product) => sum + product.count, 0)
      //
      //
      // state.countKg = itemsKg.reduce((sum, product) => sum + product.count, 0)
      // state.countPs = itemsPs.reduce((sum, product) => sum + product.count, 0)

      state.cartProducts = state.cartProducts.filter(product => product.title !== action.payload)

    },

    clearItems(state) {
      state.cartProducts = []
      state.totalPrice = 0
    },
  }
})

export const {addCartItem, minusCartItem, removeCartItem, clearItems, updateCountValues} = cartSlice.actions
export default cartSlice.reducer
