import React from 'react';
import styles from './remove.module.scss'
import {removeCartItem} from "../../../redux/slices/cartSlice";
import {useDispatch} from "react-redux";

const RemoveCartProduct = ({showRemove, setShowRemove, title}) => {

  const dispath = useDispatch()

  const hidePopup = () => {
    setShowRemove(false)
  }

  const removeItem = () => {
    dispath(removeCartItem(title))
    hidePopup()
  }

  return (
    <div className={showRemove ? styles.layout : styles.layoutHide}>
      <div className={styles.popup}>
        <p className={styles.title}>Do you really want to remove - {title} ?</p>
        <div className={styles.buttons}>
          <button
            className="button"
            onClick={removeItem}
          >
            Yes, I'm sure
          </button>
          <button
            className="button button--tomato"
            onClick={hidePopup}
          >
            No
          </button>
        </div>
      </div>
    </div>
  );
};

export default RemoveCartProduct;
