import styles from './productCard.module.scss'

const ProductCard = ({title, price, imageUrl, measure, isShowProductCard, onClickHideCard}) => {

  return (
    <div className={isShowProductCard ? styles.layout : styles.layoutHide}>
      <div className={styles.productCard}>
        <svg
          className={styles.iconClose}
          onClick={() => onClickHideCard()}
          xmlns="http://www.w3.org/2000/svg" height="512px" id="Layer_1"
          version="1.1" viewBox="0 0 512 512" width="512px">
          <path d="M437.5,386.6L306.9,256l130.6-130.6c14.1-14.1,14.1-36.8,0-50.9c-14.1-14.1-36.8-14.1-50.9,0L256,205.1L125.4,74.5
        c-14.1-14.1-36.8-14.1-50.9,0c-14.1,14.1-14.1,36.8,0,50.9L205.1,256L74.5,386.6c-14.1,14.1-14.1,36.8,0,50.9
        c14.1,14.1,36.8,14.1,50.9,0L256,306.9l130.6,130.6c14.1,14.1,36.8,14.1,50.9,0C451.5,423.4,451.5,400.6,437.5,386.6z"/>
        </svg>
        <div className={styles.contentImage}>
          <img
            src={imageUrl}
            alt={title}
            className={styles.image}
          />
        </div>
        <div className="card-content">
          <h3 className="card-title">
            {title}
          </h3>
          <p>price for 1 {measure} - {price} </p>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
