import React from 'react';

import {useSelector, useDispatch} from "react-redux";
import {setShopName} from "../redux/slices/filterSlice";

import Shops from "../components/Shops";
import Sort from "../components/Sort";
import ProductBlockSkeleton from "../components/productBlock/ProductBlockSkeleton";
import ProductBlock from "../components/productBlock/ProductBlock";
import Pagination from "../pagigination";

const Home = ({searchValue}) => {

  const dispatch = useDispatch()

  const activeShop = useSelector(state => state.filter.shop)
  const [items, setItems] = React.useState([])
  const [isLoading, setIsLoading] = React.useState(true)
  const [currenPage, setCurrentPage] = React.useState(1)

  const search = searchValue ? `search=${searchValue}` : ''
  const shop = activeShop === 'All' || !activeShop ? '' : `shop=${activeShop}`

  React.useEffect(() => {
    setIsLoading(true)
    fetch(`https://62f79a7bab9f1f8e890019ee.mockapi.io/products?${shop}&page=${currenPage}&limit=8&${search}`)
      .then(data => data.json())
      .then(data => {
        setItems(data)
        setIsLoading(false)
      })
      .catch(error => console.log(error.message()))
    // window.scrollTo(0, 0)
  }, [searchValue, currenPage, activeShop])

  const getShopProducts = (title) => {
    setIsLoading(true)
    if (title === 'All') {
      fetch(`https://62f79a7bab9f1f8e890019ee.mockapi.io/products?`)
        .then(data => data.json())
        .then(data => {
          setItems(data)
          setIsLoading(false)
        })
        .catch(error => console.log(error.message()))
      window.scrollTo(0, 0)
    } else {
      fetch(`https://62f79a7bab9f1f8e890019ee.mockapi.io/products?shop=${title}`)
        .then(data => data.json())
        .then(data => {
          setItems(data)
          setIsLoading(false)
        })
        .catch(error => console.log(error.message()))
      window.scrollTo(0, 0)
    }
  }

  const onChangeShop = (title) => {
    dispatch(setShopName(title))
  }


  const skeletons = [...new Array(9)].map((_, idx) => <ProductBlockSkeleton key={idx}/>)

  const products = items.map(product => <ProductBlock title={product.title} imageUrl={product.imageUrl}
                                                      price={product.price} measure={product.measure}
                                                      key={product.title}/>)


  return (
    <div>
      <div className="content__top">
        <Shops
          activeShop={activeShop}
          //onClickShop={(i, title) => choseShop(i, title)}
          onChangeShop={onChangeShop}
        />
        <Sort/>
      </div>
      <h2 className="content__title">All goods</h2>
      <div className="content__items">

        {
          isLoading
            ? skeletons
            : products
        }
      </div>
      <Pagination onChangePage={(num) => setCurrentPage(num)}/>
    </div>
  );
};

export default Home;
