import './scss/app.scss';

import React from "react";

import Header from "./components/Header";

import Shops from "./components/Shops";

import Sort from "./components/Sort";

import ProductBlock from "./components/productBlock/ProductBlock";

import ProductBlockSkeleton from "./components/productBlock/ProductBlockSkeleton";


function App() {

  let url = 'https://62f79a7bab9f1f8e890019ee.mockapi.io/products'
  const [products, setProducts] = React.useState([])

  //function getProducts(url) {


//}

  React.useEffect(() => {
    fetch('https://62f79a7bab9f1f8e890019ee.mockapi.io/products')
      .then(data => data.json())
      .then(data => setProducts(data))
      .catch(error => console.log(error.message()))
    setIsLoading(false)
  }, [])


  const [isLoading, setIsLoading] = React.useState(true)


  const skeletons = [...new Array(8)].map((_, idx) => <ProductBlockSkeleton key={idx}/>)

  const items = products.map(product => <ProductBlock title={product.title} imageUrl={product.imageUrl}
                                                      price={product.price} measure={product.measure}
                                                      key={product.title}/>)

  return (
    <div className="wrapper">
      <Header/>
      <div className="content">
        <div className="container">
          <div className="content__top">
            <Shops/>
            <Sort/>
          </div>
          <h2 className="content__title">All goods</h2>
          <div className="content__items">

            {
              isLoading ? skeletons : items
            }
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
