import React from 'react';
import {useSelector} from "react-redux";

const shops = ['All', 'Carrefour', 'Masterino', 'Dolphin']

const Shops = ({activeShop, onChangeShop}) => {
  const activeShopTitle = useSelector(state => state.filter.shop)

  console.log(activeShopTitle)

  return (
    <div className="shops">
      <ul>
        {
          shops.map((shopTitle) => <li
              className={shopTitle === activeShopTitle ? 'active' : ''}
              key={shopTitle}
              onClick={() => onChangeShop(shopTitle)}
            >
              {shopTitle}
            </li>
          )
        }
      </ul>
    </div>
  );
};

export default Shops;
