import React from 'react';
import axios from "axios";
import qs from 'qs';

import {useNavigate} from 'react-router-dom'
import {useSelector, useDispatch} from "react-redux";
import {setCurrentPage, setFilters} from "../redux/slices/filterSlice";
import {setProducts} from "../redux/slices/productsSlice";

import Shops from "../components/Shops";
import Sort from "../components/Sort";
import ProductBlockSkeleton from "../components/ProductBlock/ProductBlockSkeleton";
import ProductBlock from "../components/ProductBlock/ProductBlock";
import Pagination from "../pagigination";

import {sortList} from "../components/Sort";


const Home = ({searchValue}) => {

  const navigate = useNavigate()
  const dispatch = useDispatch()
  const isSearch = React.useRef(false)
  const isMounted = React.useRef(false)

  const {shop, sort, currentPage} = useSelector(state => state.filter)

  const sortType = sort.sortProperty

  const items = useSelector(state => state.products.items)

  const [isLoading, setIsLoading] = React.useState(true)

  const fetchProducts = async () => {
    setIsLoading(true)

    const searchBy = searchValue ? `search=${searchValue}` : ''
    const shopBy = (shop === 'All' || !shop) ? '' : `shop=${shop}`
    const sortBy = `sortBy=${sortType}`

    try {
      const {data} = await axios.get(`https://62f79a7bab9f1f8e890019ee.mockapi.io/products?${shopBy}&page=${currentPage}&${sortBy}&limit=8&${searchBy}&order=asc`)
      dispatch(setProducts(data))
    } catch (error) {
      console.log(error)
      alert('Error fetching products')
    } finally {
      setIsLoading(false)
    }
    window.scrollTo(0, 0)
  }

  React.useEffect(() => {
    if (window.location.search) {
      const params = qs.parse(window.location.search.substring(1))

      const sort = sortList.find(obj => obj.sortProperty === params.sortProperty)

      dispatch(setFilters({
          ...params,
          sort
        })
      )
      isSearch.current = true
    }
  }, [])


  React.useEffect(() => {
    window.scrollTo(0, 0)

    if (!isSearch.current) {
      fetchProducts()
    }

    isSearch.current = false

  }, [searchValue, currentPage, shop, sortType])


  React.useEffect(() => {

    if (isMounted.current) {
      const queryString = qs.stringify({
        sortProperty: sort.sortProperty,
        shop,
        currentPage
      })

      navigate(`?${queryString}`)
    }
    isMounted.current = true
  }, [searchValue, currentPage, shop, sortType])

  const onChangePage = (num) => dispatch(setCurrentPage(num))

  const skeletons = [...new Array(9)].map((_, idx) => <ProductBlockSkeleton key={idx}/>)
  const products = items
    .map(product => <ProductBlock title={product.title} imageUrl={product.imageUrl}
                                  price={product.price} measure={product.measure}
                                  key={product.title}/>)

  return (
    <div>
      <div className="content__top">
        <Shops/>
        <Sort/>
      </div>
      <h2 className="content__title">All goods</h2>
      <div className="content__items">
        {
          isLoading
            ? skeletons
            : products
        }
      </div>
      <Pagination currentPage={currentPage} onChangePage={onChangePage}/>
    </div>
  );
};

export default Home;
